universityData = []


def get_university_data(session, country):
    country = urllib.parse.quote_plus(country)
    url = "http://universities.hipolabs.com/search?country=" + country

    response = session.get(url)
    data = response.json()

    #sorting data 
    for item in data:
        university = (item['name'],item['country'],item['web_pages'][0])
        
        global universityData
        universityData.append(university)

    


if __name__ == '__main__':
    with requests.Session() as session:
        sessionCount = len(countryList)

        # multithreading to speed up the API calls and save time.
        with ThreadPoolExecutor(max_workers=100) as executor:
            executor.map(get_university_data, [session]*sessionCount, countryList)
            executor.shutdown(wait=True)

    #Save the data for later use or do as you wish
    db.create_table()
    db.save_university_data(universityData)
